import React, { Component } from 'react';
import './App.css';
var request = require('request');



class App extends Component {
  state = {
    //response the get request come from the /api/hello route
    response: '',
    drug:'',
    disease:'',
    type:'',
    responseToPost: '',// response to submit
  };

  /*callApi method to interact with  GET Express API route
  we call this method in componentDidMount and finally set the state to the API response
  */
 componentDidMount() {
    this.callApi()
      .then(res => this.setState({ response: res.express }))
      .catch(err => console.log(err));
  }

  /*calls our POST Express API route then saves the response to
   state and displays a message to the user: I received your POST request.
   This is what you sent me: [message from input].*/


  callApi = async () => {
    const response = await fetch('/api/');

    const body = await response.json();


    if (response.status !== 200) throw Error(body.message);

    return body;

  };

 handleSubmit =  e =>{

     e.preventDefault();

  /*   const response = await fetch('/api/submit', {
       method: 'POST',

       headers: {
         'Content-Type': 'application/json'
       },

       body:JSON.stringify({
          drug: this.state.drug,
            disease: this.state.disease,
              type: this.state.type,

         })


     });
     //get the response
     const body = await response.text();

     this.setState({ responseToPost: body });
*/
var xml='<ENVELOPE><HEADER><TALLYREQUEST>Export Data</TALLYREQUEST></HEADER><BODY><EXPORTDATA><REQUESTDESC><REPORTNAME>Stock Summary</REPORTNAME><STATICVARIABLES><EXPLODEFLAG>Yes</EXPLODEFLAG><SVEXPORTFORMAT>$$SysName:XML</SVEXPORTFORMAT><ACCOUNTTYPE>All Inventory Masters</ACCOUNTTYPE></STATICVARIABLES></REQUESTDESC></EXPORTDATA></BODY></ENVELOPE>'
request.post(
    {url:'http://127.0.0.1:3000/api/submit',
    body :xml ,
    headers: {'Content-Type': 'text/xml'}
    },
    function (error, response, body) {
        if (!error && response.statusCode == 200) {
            console.log(body)
        }
    }
);

   }

  render() {
    return (
      <div className="App">
      <pre>{this.state.response}</pre>


        <form onSubmit={this.handleSubmit}>
          <p>
            <strong>Post to Server:</strong>
          </p>
          drug
          <input
            type="text"
            value={this.state.drug}
            onChange={e => this.setState({ drug: e.target.value })}
          />

          disease
          <input
            type="text"
            value={this.state.disease}
            onChange={e => this.setState({ disease: e.target.value })}
          />
          type

          <select value={this.state.type} onChange={e => this.setState({ type: e.target.value })}>
          <option >select value</option>
           <option value="1">1</option>
           <option value="2">2</option>

         </select>


          <button type="submit">Submit</button>
        </form>
        <pre>{this.state.responseToPost}</pre>

      </div>
    );
  }
}

export default App;
