import React, { Component } from 'react';

import logo from './logo.svg';

import './App.css';
import axios from 'axios';

class App extends Component {
  state = {
    //response the get request come from the /api/hello route
    response: '',
  //  post: '',
    drug:'',
    disease:'',
    type:'',
    responseToPost: '',// response to submit
  };

 componentDidMount() {
    this.callApi()
      .then(res => this.setState({ response: res.express }))
      .catch(err => console.log(err));
  }
/*callApi method to interact with our GET Express API route
we call this method in componentDidMount and finally set the state to the API response
*/
  callApi = async () => {
    const response = await fetch('/api/hello');

    const body = await response.json();


    if (response.status !== 200) throw Error(body.message);

    return body;
  };
/*calls our POST Express API route then saves the response to
 state and displays a message to the user: I received your POST request.
 This is what you sent me: [message from input].*/
/*handleSubmit = async e => {

    e.preventDefault();

    const response = await fetch('/api/world', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/JSON'
      },
      body: JSON.stringify({ drug: this.state.drug }),


    });

    const body = await response.text();


    this.setState({ responseToPost: body });

  };
*/
  handleSubmit = e => {
      e.preventDefault();

      //const { post } = this.state;
      const { drug } = this.state;
      const { disease } = this.state;
      const { type } = this.state;

      const request = {
        //post,
        drug,
        disease,
        type

      };

      axios
        .post('/api/world', request)
        .then(() => console.log('data searched'))
        .catch(err => {
          console.error(err);
        });
    };







  render() {
    return (
      <div className="App">

        <p>/*{this.state.response}*/</p>
        <form onSubmit={this.handleSubmit}>
          <p>
            <strong>Post to Server:</strong>
          </p>
        drug
          <input
            type="text"
            value={this.state.drug}
            onChange={e => this.setState({ drug: e.target.value })}
          />

          disease
          <input
            type="text"
            value={this.state.disease}
            onChange={e => this.setState({ disease: e.target.value })}
          />
          type

          <select value={this.state.type} onChange={e => this.setState({ type: e.target.value })}>
           <option value="1">1</option>
           <option value="2">2</option>

         </select>




          <button type="submit">Submit</button>
        </form>
        <p>{this.state.responseToPost}</p>

      </div>
    );
  }
}

export default App;
