import React, { Component } from 'react';
import './App.css';
var request = require('request');
//var jsonxml = require('jsontoxml');
//var request = require('request');
  // const axios = require('axios');
var js2xmlparser = require("js2xmlparser");//to convert from json to xml
//var parseString = require('xml2js').parseString;//to convert from xml to js object
var x2js =require('x2js');


class App extends Component {
  state = {
    //response the get request come from the /api/hello route
    response: '',
    drug:'',
    disease:'',
    type:'',
    responseToPost: '',// response to submit
  };

 componentDidMount() {
    this.callApi()
      .then(res => this.setState({ response: res.express }))
      .catch(err => console.log(err));
  }
/*callApi method to interact with our GET Express API route
we call this method in componentDidMount and finally set the state to the API response
*/
  callApi = async () => {
    const response = await fetch('/api/');

    const body = await response.json();


    if (response.status !== 200) throw Error(body.message);

    return body;
  };
/*calls our POST Express API route then saves the response to
 state and displays a message to the user: I received your POST request.
 This is what you sent me: [message from input].*/

 handleSubmit =  e =>{

  e.preventDefault();
  /*var request=JSON.stringify({
     drug: this.state.drug,
       disease: this.state.disease,
         type: this.state.type,

    });
    var xr=js2xmlparser.parse("request", request);




     const response = await fetch('/api/submit', {
       method: 'POST',

       headers: {
         'Content-Type': 'application/json'
       },

       body: JSON.stringify({
          drug: this.state.drug,
            disease: this.state.disease,
              type: this.state.type,

         })


     });

     const body = await response.text();


     this.setState({ responseToPost: body });*/
     /*var r=JSON.stringify({
        drug: this.state.drug,
          disease: this.state.disease,
            type: this.state.type,

       });*/
       var r={
          drug: this.state.drug,
            disease: this.state.disease,
              type: this.state.type,

         };
      // var xr=js2xmlparser.parse("request", r);

       var options = {
          method: 'POST',
          keepAlive: false,
          url: 'http://127.0.0.1:3000/api/submit',
          headers: {

            'Content-Type': 'application/x-www-form-urlencoded',

          },
          body:x2js.js2xml(r)
        };
         request(options, function (error, response, body) {
        console.log(body);
     });



   }


  /*handleSubmit = e => {
      e.preventDefault();

      //const { post } = this.state;
      const { drug } = this.state;
      const { disease } = this.state;
      const { type } = this.state;

      const request = {
        //post,
        drug,
        disease,
        type

      };

      axios
        .post('/api/world', request)
        .then(() => console.log('data searched'))
        .catch(err => {
          console.error(err);
        });
    };
*/






  render() {
    return (
      <div className="App">


        <form onSubmit={this.handleSubmit}>
          <p>
            <strong>Post to Server:</strong>
          </p>
        drug
          <input
            type="text"
            value={this.state.drug}
            onChange={e => this.setState({ drug: e.target.value })}
          />

          disease
          <input
            type="text"
            value={this.state.disease}
            onChange={e => this.setState({ disease: e.target.value })}
          />
          type

          <select value={this.state.type} onChange={e => this.setState({ type: e.target.value })}>
          <option >select value</option>
           <option value="1">1</option>
           <option value="2">2</option>

         </select>




          <button type="submit">Submit</button>
        </form>
        <pre>{this.state.responseToPost}</pre>

      </div>
    );
  }
}

export default App;
